Button:
import PropTypes from "prop-types"
import React from "react"
import styled from "styled-components"
import {
  color,
  borderRadius,
  spacing,
  targetSize,
  font,
  fontFamily,
  fontWeight,
  applyVariants
} from "../../theming"
import { themePropTypes, commonPropTypes } from "../../constants/themeContants"

const tinycolor = require("tinycolor2")

const tProps = {
  button: {
    name: "Button",
    description: "Button styles",
    type: themePropTypes.style
  }
}

const ButtonComp = styled.button`
  background-color: ${color.primary};
  color: ${p =>
    tinycolor
      .mostReadable(color.primary()(p), ["#fff"], {
        includeFallbackColors: true,
        level: "AA",
        size: "large"
      })
      .toString()};
  border-width: 0;
  border-radius: ${borderRadius};
  padding: ${spacing.tiny} ${spacing.medium};
  cursor: pointer;
  min-height: ${targetSize.normal};
  text-decoration: none;
  text-align: center;
  display: inline-block;
  font-size: ${font.base};
  font-family: ${fontFamily.body};
  line-height: 32px;
  font-weight: ${fontWeight.bold};
  white-space: nowrap;
  justify-content: center;
  align-items: center;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  transition: background 0.2s ease;

  &:hover,
  &:focus {
    outline: none;
    background-color: ${p =>
      tinycolor(color.primary()(p))
        .darken(5)
        .toString()};
    color: ${p =>
      tinycolor
        .mostReadable(tinycolor(color.primary()(p)).darken(5), ["#fff"], {
          includeFallbackColors: true,
          level: "AA",
          size: "large"
        })
        .toString()};
  }

  ${applyVariants(tProps.button.name)};
`
/**
 * Button component. Use to click stuff
 */
const Button = props => <ButtonComp {...props} />

const defaultProps = {
  children: "button",
  onClick: null
}

const propTypes = {
  /**
   * The children of the lamb
   */
  children: commonPropTypes.children.isRequired,
  /**
   * Click callback. Why else have a button?
   * @export
   */
  onClick: PropTypes.func
}

Button.defaultProps = defaultProps
Button.propTypes = propTypes
Button.themeProps = tProps

export default Button

CodeRenderer:
import React, { Component } from "react"
import PropTypes from "prop-types"
import Prism from "prismjs"
import CodeWrapper from "./CodeWrapper"
import "prismjs/components/prism-markup"

class CodeRenderer extends Component {
  render() {
    const { code, language } = this.props
    let grammar = null
    let className = ""

    switch (language) {
      case "markup": {
        className = "language-markup"
        grammar = Prism.languages.html
        break
      }

      case "javascript": {
        className = "language-js"
        grammar = Prism.languages.javascript
        break
      }

      case "java": {
        className = "language-java"
        grammar = Prism.languages.java
        break
      }

      case "csharp": {
        className = "language-csharp"
        grammar = Prism.languages.csharp
        break
      }

      default:
        break
    }

    const html = {
      __html: Prism.highlight(code, grammar, language)
    }
    return (
      <CodeWrapper className={className}>
        <code className={className} dangerouslySetInnerHTML={html} />
      </CodeWrapper>
    )
  }
}

CodeRenderer.propTypes = {
  code: PropTypes.string.isRequired,
  language: PropTypes.oneOf(["markup", "javascript", "java", "csharp"])
    .isRequired
}

export default CodeRenderer

CopyToClipboardComponent:
import PropTypes from "prop-types"
import styled from "styled-components"
import CopyToClipboard from "../../hoc/Clipboard/CopyToClipboard"
import React from "react"
import Upload from "../Icons/Upload"
import Check from "../Icons/Check"
import ThemeComponent from "../Theme/ThemeComponent"
import { applyVariants } from "../../theming"
import { themePropTypes, commonPropTypes } from "../../constants/themeContants"

class CopyToClipboardComponent extends React.PureComponent {
  constructor(props, context) {
    super(props, context)

    this.handleClick = this.handleClick.bind(this)

    this.state = {
      copied: false
    }
  }

  handleClick(copy) {
    copy(this.props.copyText)
    this.setState({ copied: true }, () => {
      setTimeout(() => {
        this.setState({ copied: false })
      }, 1000)
    })
  }

  render() {
    return (
      <CopyToClipboard children={this.props.children}>
        {({ copy }) => (
          <ClipboardWrapper
            onClick={() => {
              this.handleClick(copy)
            }}
          >
            {this.props.children}
            {this.state.copied ? <Copied /> : <Copy />}
          </ClipboardWrapper>
        )}
      </CopyToClipboard>
    )
  }
}

CopyToClipboardComponent.themeProps = {
  wrapper: {
    name: "CopyToClipboardComponent_Wrapper",
    description: "CopyToClipboardComponent wrapper style",
    type: themePropTypes.style
  },
  icon: {
    name: "CopyToClipboardComponent_Icon_style",
    description: "Icon style",
    type: themePropTypes.style
  },
  copyComponent: {
    name: "CopyToClipboardComponent_CopyComponent",
    description: "Copy component",
    type: themePropTypes.component
  },
  copiedComponent: {
    name: "CopyToClipboardComponent_CopiedComponent",
    description: "Copied component",
    type: themePropTypes.component
  }
}

const ClipboardWrapper = styled.button`
  &:hover {
    outline: none;
  }
  ${applyVariants(CopyToClipboardComponent.themeProps.wrapper)};
`

const CopiedComponent = ({ ...props }) => (
  <ThemeComponent
    tagName={CopyToClipboardComponent.themeProps.copiedComponent}
    fallback={Check}
    {...props}
  />
)

const CopyComponent = ({ ...props }) => (
  <ThemeComponent
    tagName={CopyToClipboardComponent.themeProps.copyComponent}
    fallback={Upload}
    {...props}
  />
)

export const Copy = styled(CopyComponent)`
  ${applyVariants(CopyToClipboardComponent.themeProps.icon)};
`

export const Copied = styled(CopiedComponent)`
  ${applyVariants(CopyToClipboardComponent.themeProps.icon)};
`

CopyToClipboardComponent.propTypes = {
  children: commonPropTypes.children.isRequired,
  copyText: PropTypes.string.isRequired
}

export default CopyToClipboardComponent

Donut:
import React from "react"
import PropTypes from "prop-types"
import styled from "styled-components"
import { applyVariants, color } from "../../../theming"
import { themePropTypes } from "../../../constants/themeContants"

const Donut = ({ progress, className, warningThreshold, ...rest }) => {
  const dash = 57
  const dashoffset = dash * progress - dash
  return (
    <Wrapper
      viewBox="0 0 24 24"
      width="24"
      height="24"
      className={className}
      {...rest}
    >
      <title>{progress * 100}%</title>
      <g fill="none" fillRule="evenodd" strokeWidth="6">
        <Bg cx="12" cy="12" r="9" />
        <Value
          cx="12"
          cy="12"
          r="9"
          transform="rotate(-90 12 12)"
          dash={dash}
          dashoffset={dashoffset}
          progress={progress}
          warningThreshold={warningThreshold}
        />
      </g>
    </Wrapper>
  )
}

Donut.themeProps = {
  donut: {
    name: "donut",
    description: "Wrapper style for donut",
    type: themePropTypes.style
  },
  donutBackground: {
    name: "donut_bg",
    description: "Circle style for donut background",
    type: themePropTypes.style
  },
  donutValue: {
    name: "donut_value",
    description: "Value style for donut",
    type: themePropTypes.style
  }
}

const Wrapper = styled.svg`
  transform: rotateY(180deg);
  ${applyVariants(Donut.themeProps.donut.name)};
`

const Bg = styled.circle`
  stroke: ${color.line};
  ${applyVariants(Donut.themeProps.donutBackground.name)};
`

const Value = styled.circle`
  stroke: ${p =>
    p.progress >= p.warningThreshold ? color.warning : color.primary};
  stroke-dasharray: ${p => p.dash};
  stroke-dashoffset: ${p => p.dashoffset};
  ${applyVariants(Donut.themeProps.donutValue.name)};
`

Donut.defaultProps = {
  className: "",
  warningThreshold: 0.9
}

Donut.propTypes = {
  progress: PropTypes.number.isRequired,
  warningThreshold: PropTypes.number,
  className: PropTypes.string
}

export default Donut

Fraction:
import React from "react"
import PropTypes from "prop-types"
import styled, { css } from "styled-components"
import hideVisually from "../../../theming/styles/hideVisually"
import { themePropTypes } from "../../../constants/themeContants"
import { applyVariants, color, spacing } from "../../../theming"

const Fraction = ({
  value,
  max,
  maxComponent,
  valueComponent,
  className,
  onClick,
  ...rest
}) => {
  const fractionArray = [...Array(max)].map((e, index) => index < value)
  const unfilled = maxComponent || <DesaturatedDot />
  const filled = valueComponent || <ColoredDot />

  const addKey = (compo, index, handleClick) =>
    React.cloneElement(compo, {
      key: `dot-${index}`,
      onClick: handleClick || null
    })

  return (
    <FractionWrapper className={className} {...rest}>
      {fractionArray.map((dot, index) =>
        dot
          ? addKey(filled, index, () => (onClick ? onClick(index) : null))
          : addKey(unfilled, index, () => (onClick ? onClick(index) : null))
      )}
      <Hide>
        {value} / {max}
      </Hide>
    </FractionWrapper>
  )
}

const Hide = styled.span`
  ${hideVisually};
`

Fraction.themeProps = {
  wrapper: {
    name: "fraction",
    description: "Wrapper style",
    type: themePropTypes.style
  },
  circle: {
    name: "fraction_circle",
    description: "Circle style",
    type: themePropTypes.style
  },
  coloredDot: {
    name: "fraction_colored_dot",
    description: "Colored dot style",
    type: themePropTypes.style
  },
  desaturatedDot: {
    name: "fraction_desaturated_dot",
    description: "Desaturated dot style",
    type: themePropTypes.style
  }
}

const circle = css`
  width: 12px;
  height: 12px;
  border-radius: 50%;
  ${applyVariants(Fraction.themeProps.circle.name)};
`

const ColoredDot = styled.div`
  ${circle};
  background-color: ${color.primary};
  ${applyVariants(Fraction.themeProps.coloredDot.name)};
`

const DesaturatedDot = styled.div`
  ${circle};
  background-color: ${color.line};
  ${applyVariants(Fraction.themeProps.desaturatedDot.name)};
`

const FractionWrapper = styled.div`
  display: flex;
  flex-direction: row;
  > * {
    margin-right: ${spacing.tiny};
  }
  ${applyVariants(Fraction.themeProps.wrapper.name)};
`

Fraction.defaultProps = {
  max: 5,
  value: 5,
  maxComponent: null,
  valueComponent: null,
  className: "",
  onClick: null
}

Fraction.propTypes = {
  /**
   * Value
   */
  value: PropTypes.number.isRequired,
  /**
   * Max
   */
  max: PropTypes.number.isRequired,
  maxComponent: PropTypes.element,
  valueComponent: PropTypes.element,
  className: PropTypes.string,
  onClick: PropTypes.func
}

export default Fraction

