Loading:
import React from "react"
import PropTypes from "prop-types"
import styled, { keyframes } from "styled-components"
import { themePropTypes } from "../../../constants/themeContants"
import ThemeComponent from "../../Theme/ThemeComponent"
import { applyVariants, color, spacing } from "../../../theming"
import useTimer from "../../../hooks/useTimer"

const DefaultLoading = ({ className, variant, ...rest }) => (
  <SignalSpinner className={className} variant={variant} {...rest} />
)

export const COMPONENT_LOADING = "COMPONENT_LOADING"

const themeProps = {
  component: {
    name: "COMPONENT_LOADING",
    description: "Component for loading",
    type: themePropTypes.component
  },
  loading: {
    name: "LOADING",
    description: "Loading style",
    type: themePropTypes.style
  }
}

const Loading = ({ className, idleTime, ...rest }) => {
  const isReady = useTimer(idleTime)

  // Wait before rendering
  if (!isReady) {
    return null
  }
  return (
    <ThemeComponent
      tagName={themeProps.component.name}
      fallback={DefaultLoading}
      {...rest}
    />
  )
}

const spinnerBubble1 = keyframes`
  0% {
    color: ${color.primary};
    transform: translate(-50%, -50%) scale(1);
    opacity: 1;
  }
  100% {
    color:  ${color.secondary};
    transform: translate(-50%, -50%) scale(4.8);
    opacity: 0;
  }
`

const spinnerBubble2 = keyframes`
  0% {
    color: ${color.secondary};
    transform: translate(-50%, -50%) scale(1);
    opacity: 1;
  }
  100% {
    color: ${color.primary};
    transform: translate(-50%, -50%) scale(4.8);
    opacity: 0;
  }
`

const SignalSpinner = styled.div`
  position: relative;
  display: block;
  width: ${spacing.large};
  height: ${spacing.large};
  color: ${color.primary};

  &::before,
  &::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 50%;
    width: 10px;
    height: 10px;
    opacity: 0;
    box-shadow: 0 0 0 1px;
    border-radius: 50%;
    transform: translate(-50%, -50%) scale(1);
    transform-origin: center;
  }

  &::before {
    animation: ${spinnerBubble1} 2.6s ease-out 1.3s infinite;
  }

  &::after {
    animation: ${spinnerBubble2} 2.6s ease-out 0s infinite;
  }
  ${applyVariants(themeProps.loading.name)};
`

Loading.propTypes = { className: PropTypes.string, variant: PropTypes.string }
Loading.themeProps = themeProps

export default Loading

ProgressBar:
import React from "react"
import PropTypes from "prop-types"
import styled from "styled-components"
import { ScaleInX } from "@staccx/animations"
import { themePropTypes } from "../../../constants/themeContants"
import { applyVariants, color, spacing, font } from "../../../theming"

const ProgressBar = ({
  progress,
  className,
  warningThreshold,
  ...otherProps
}) => {
  if (progress > 1) {
    console.warn("Progress is above max (1). Please enter correct value")
    progress *= 0.01
  }
  return (
    <Bg className={className} {...otherProps}>
      <Bar width={progress} warningThreshold={warningThreshold}>
        <Progress>
          {progress >= 1 ? "100" : Math.round(progress * 100)}%
        </Progress>
      </Bar>
    </Bg>
  )
}

const themeProps = {
  background: {
    name: "progress_bar",
    description: "Progress bar background style",
    type: themePropTypes.style
  },
  bar: {
    name: "progress_bar_bar",
    description: "Progress bar bar style",
    type: themePropTypes.style
  },
  progress: {
    name: "progress_bar_progress",
    description: "Progress bar progress style",
    type: themePropTypes.style
  }
}

const Bg = styled.div`
  background: ${color.subtleHover};
  display: block;
  height: ${spacing.medium};
  min-width: 200px;
  border-radius: ${spacing.small};
  position: relative;
  overflow: hidden;
  border: 3px solid ${color.subtleHover};
  -webkit-mask-image: -webkit-radial-gradient(
    white,
    black
  ); /* to remove transition-bug */

  ${applyVariants(themeProps.background.name)};
`

const Bar = styled.div`
  position: absolute;
  background-color: ${p =>
    p.width >= p.warningThreshold ? color.warning : color.primary};
  left: 0;
  height: 100%;
  width: ${p => (p.width > 1 ? "100" : p.width * 100)}%;
  border-radius: 0 ${spacing.small} ${spacing.small} 0;
  transform: scaleX(0);
  animation: ${ScaleInX} 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275) 0.3s 1
    forwards;
  transform-origin: left center;
  ${applyVariants(themeProps.bar.name)};
`

const Progress = styled.span`
  position: absolute;
  right: ${spacing.small};
  line-height: 18px;
  font-size: ${font.tiny};
  color: ${color.white};
  ${applyVariants(themeProps.progress.name)};
`

ProgressBar.defaultProps = {
  className: "",
  warningThreshold: 0.9
}

ProgressBar.propTypes = {
  /**
   * Progress normalized (0-1)
   */
  progress: PropTypes.number.isRequired,
  warningThreshold: PropTypes.number,
  className: PropTypes.string
}

ProgressBar.themeProps = themeProps

export default ProgressBar

Expand:
import React, { Component } from "react"
import PropTypes from "prop-types"
import styled from "styled-components"
import { BounceIn } from "@staccx/animations"
import Caret from "../Icons/Caret"
import { commonPropTypes, themePropTypes } from "../../constants/themeContants"
import ThemeComponent from "../Theme/ThemeComponent"
import { applyVariants, color, spacing } from "../../theming"

class Expand extends Component {
  constructor(props) {
    super(props)
    this.handleExpand = this.handleExpand.bind(this)
    this.state = {
      isExpanded: this.props.expanded
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.expanded !== undefined) {
      this.setState({ isExpanded: nextProps.expanded })
    }
  }

  handleExpand(event) {
    this.setState({
      isExpanded: !this.state.isExpanded
    })
    if (this.props.onClick) {
      this.props.onClick(event)
    }
  }

  render() {
    const {
      children,
      className,
      onClick,
      title,
      hideIcon,
      variant,
      ...restProps
    } = this.props

    const { isExpanded } = this.state

    return (
      <ExpandWrapper className={className} variant={variant} {...restProps}>
        {React.Children.map(children, (child, i) => {
          // Ignore the first child
          if (i < 1) {
            return (
              <ExpandBtn
                title={title}
                isExpanded={isExpanded}
                onClick={this.handleExpand}
                aria-expanded={isExpanded}
                aria-controls={title}
                id={title + "2"}
                variant={variant}
              >
                {child}
                {!hideIcon && (
                  <ExpandIcon variant={variant} isExpanded={isExpanded} />
                )}
              </ExpandBtn>
            )
          }
          return (
            isExpanded && (
              <ExpandItem
                id={title}
                aria-labelledby={title + "2"}
                variant={variant}
              >
                {child}
              </ExpandItem>
            )
          )
        })}
      </ExpandWrapper>
    )
  }
}

Expand.themeProps = {
  iconComponent: {
    name: "COMPONENT_EXPAND_ICON",
    description: "Icon shown. Defaults to Caret",
    type: themePropTypes.component
  },
  icon: {
    name: "EXPAND_ICON",
    description: "Icon style",
    type: themePropTypes.style
  },
  wrapper: {
    name: "EXPAND_WRAPPER",
    description: "Wrapper style",
    type: themePropTypes.style
  },
  button: {
    name: "EXPAND_BTN",
    description: "Button style",
    type: themePropTypes.style
  },
  item: {
    name: "EXPAND_ITEM",
    description: "Item style",
    type: themePropTypes.style
  }
}

const IconComponent = ({ ...props }) => (
  <ThemeComponent
    tagName={Expand.themeProps.iconComponent.name}
    fallback={Caret}
